import React, { useState, useEffect } from 'react';
import logo from '../../Assets/images/home/logo.png';
import { Link } from 'react-router-dom';

function Navbar() {
    const [nav, setNav] = useState(false);

    const changeBackground = () => {
        if (window.scrollY >= 50) {
            setNav(true);
        } else {
            setNav(false);
        }
    };

    useEffect(() => {
        window.addEventListener('scroll', changeBackground);

        // Cleanup: remove the event listener when the component unmounts
        return () => {
            window.removeEventListener('scroll', changeBackground);
        };
    }, []); // Empty dependency array means this useEffect runs once when component mounts and cleans up when it unmounts

    return (
        <nav className={nav ? 'nav active' : 'nav'}>
            <button className='logo' onClick={() => { /* Handle logo click here */ }}>
                <img src={logo} alt='Company Logo'/>
            </button>
            <input type='checkbox' className='menu-btn' id='menu-btn'/>
            <label className='menu-icon' htmlFor='menu-btn'>
                <span className='nav-icon'></span>
            </label>
            <ul className='menu'>
                <li><button className='active' onClick={() => { /* Handle Home click here */ }}>Home</button></li>
                <li><button onClick={() => { /* Handle Features click here */ }}>Features</button></li>
                <li><button onClick={() => { /* Handle About click here */ }}>About</button></li>
                <li><button onClick={() => { /* Handle UI SS click here */ }}>UI SS</button></li>
                <li><button onClick={() => { /* Handle Login click here */ }}>Login</button></li>
            </ul>
        </nav>
    );
}

export default Navbar;
