import React from 'react'
import './styles.css';


function Presentation() {
  return (
    <div id='Presentation'>
        <h1>UI Dashboard</h1>
        <p>Welcome to our dashboard, the heart of our password manager. Designed with simplicity in mind,
           its intuitive interface ensures that users of all tech levels can navigate with ease. 
           Every feature is thoughtfully placed, ensuring quick access and a seamless experience. With clean lines, straightforward icons, and a clutter-free layout, managing your passwords has never been this effortless.</p>
    </div>
  )
}

export default Presentation;
