import styles from './LoginSignup.module.css';

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signInWithEmailAndPassword, createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from '../../firebase';

import userIcon from '../../Assets/images/LoginSignup/person.png';
import emailIcon from '../../Assets/images/LoginSignup/email.png';
import passwordIcon from '../../Assets/images/LoginSignup/password.png';
import logoIcon from '../../Assets/images/LoginSignup/logo.png';

export const LoginSignup = () => {
    // ... [rest of your code remains unchanged]

    return (
        <div className={styles.container}>
            <div className={styles.logo}>
                <img src={logoIcon} alt="Logo" />
                <span className={styles.logoText}>Password Protector</span>
            </div>
            <div className={styles.header}>
                <div className={styles.text}>{headingText}</div>
            </div>
            <div className={styles.submitContainer}>
                <span className={styles.additionalText}>{additionalText}</span>
                <div className={isLogin ? styles.submit + ' ' + styles.gray : styles.submit} onClick={toggleAction}>
                    {buttonText}
                </div>
            </div>
            <div className={styles.inputs}>
                {!isLogin && (
                    <div className={styles.input}>
                        <img src={userIcon} alt="User" />
                        <input 
                            placeholder="name" 
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                        />
                    </div>
                )}
                <div className={styles.input}>
                    <img src={emailIcon} alt="Email" />
                    <input 
                        placeholder="email address" 
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </div>
                <div className={styles.input}>
                    <img src={passwordIcon} alt="Password" />
                    <input 
                        placeholder="password" 
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                </div>
            </div>
            <div className={styles.continueButton} onClick={handleContinue}>
                Continue
            </div>
            {isLogin && (
                <div className={styles.forgotPassword}>
                    <span>Forgot your password?</span>
                </div>
            )}
        </div>
    );
};
